    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        tipos = con.devolverArrayListTipos();
        Combo.addItem("------");
        for(String e: tipos)
        {
            Combo.addItem(e);
        }
        
    }//GEN-LAST:event_formWindowOpened

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        VentanaPrin abrir = new VentanaPrin ();
        abrir.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(Texto.getText().equals(""))
        JOptionPane.showMessageDialog(this, "No ha seleccionado un tipo", "Error", 0);
        else
        {
            con.editarTipo(Texto.getText(), Combo.getSelectedIndex()-1);
            JOptionPane.showMessageDialog(this, "Se ha creado el tipo exitosamente", "Informaci√≥n", 1);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if(Combo.getSelectedIndex()==0)
        JOptionPane.showMessageDialog(this, "No ha seleccionado un tipo", "Error", 0);
        else
        {
            Tipos temporal = con.devolverTipo(Combo.getSelectedIndex()-1);
            Texto.setText(temporal.getNombre());
        }
    }//GEN-LAST:event_jButton2ActionPerformed

